import React, { useState } from 'react';
import { Link, useNavigate } from "react-router-dom";
import {
  Avatar,
  Button,
  CssBaseline,
  TextField,
  FormControl,
  FormControlLabel,
  Checkbox,
  FormHelperText,
  Grid,
  Box,
  Typography,
  Container,
} from '@mui/material/';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const Register = () => {
  const theme = createTheme();
  const [checked, setChecked] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");
  const navigate = useNavigate()
  const [duplicatePopup, setDuplicatePopup] = useState(false);
  const [successPopup, setSuccessPopup] = useState(false);
  const [failurePopup, setFailurePopup] = useState(false);
  const [failure2Popup, setFailure2Popup] = useState(false);

  // 동의 체크
  const handleAgree = (event) => {
    setChecked(event.target.checked);
  };

  // form 전송

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };
  const handlePasswordChange = (e) => {
    //console.log("Password:", password);
    setPassword(e.target.value);
  };

  const handlePassword2Change = (e) => {
    //console.log("Password2:", password2);
    setPassword2(e.target.value);
  };

  const duplicateHandler = (e) => {
    //중복 확인 성공하면 성공팝업, 아니면 실패 팝업
    //const isDuplicate = checkDuplicate(); // 중복 확인 로직을 구현한 함수
    /* if (isDuplicate) {
      setDuplicateError(true); // 중복된 이메일이 있다면 에러 설정
    } else {
      setDuplicateError(false); // 중복된 이메일이 없다면 에러 해제
      setSuccessPopup(true); // 중복 확인 성공 팝업 설정
    } */
    setDuplicatePopup(false); // 중복된 이메일이 없다면 에러 해제
    setSuccessPopup(true); // 중복 확인 성공 팝업 설정
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Perform register logic here, e.g., send email and password to an API for authentication
    // password = password2 이면 로그인 아니면 팝업
    console.log("Password:", password);
    console.log("Password2:", password2);

    if (password === password2 && successPopup === true){
      console.log("Email:", email);
      console.log("Password:", password);
      setPassword(password);

      setSuccessPopup(true);
      navigate("/");
    } else if(password !== password2 && successPopup === true){
      setFailurePopup(true);
      setFailure2Popup(false);
    } else if(password === password2 && successPopup !== true){
      setFailurePopup(false);
      setFailure2Popup(true);
    } else{
      setFailurePopup(true);
      setFailure2Popup(true);
    }

  };


  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: '#3c407f' }} />
          <Typography component="h1" variant="h5">
            회원가입
          </Typography>
          <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
            <FormControl component="fieldset" variant="standard">
              <Grid container spacing={2}>
                <Grid item xs={12}>
                  <TextField
                    required
                    autoFocus
                    fullWidth
                    type="email"
                    id="email"
                    name="email"
                    label="이메일 주소"
                  />
                  <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    sx={{ mt: 3, mb: 2 }}
                    size="small"
                    onclick = {duplicateHandler}
                    >
                    중복 검사
                  </Button>
                </Grid>
                {duplicatePopup && <div style={{ color: 'red' }}>중복된 이메일입니다.</div>} {/* 중복 에러 표시 */}
                {successPopup && <div>성공했습니다.</div>} {/* 성공 팝업 표시 */}
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    type="password"
                    id="password"
                    name="password"
                    label="비밀번호 (숫자+영문자+특수문자 8자리 이상)"
                    onChange={handlePasswordChange}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    type="password"
                    id="password2"
                    name="password2"
                    label="비밀번호 재입력"
                    onChange={handlePassword2Change}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField required fullWidth id="name" name="name" label="이름" />
                </Grid>
                <Grid item xs={12}>
                  <FormControlLabel
                    control={<Checkbox onChange={handleAgree} color="primary" />}
                    label="회원가입 약관에 동의합니다."
                  />
                </Grid>
              </Grid>
              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2 }}
                size="large"
                onClick={handleSubmit}
              >
                회원가입
              </Button>
            </FormControl>
          </Box>
        </Box>
        {failure2Popup && <div>이메일 중복 확인을 한 후 제출 버튼을 눌러주세요.</div>} {/* 실패 팝업 표시 */}
        {failurePopup && <div>비밀번호와 비밀번호 확인이 일치하지 않습니다.</div>} {/* 실패 팝업 표시 */}
      </Container>
    </ThemeProvider>
  );
};
export default Register;
